mapper.sql:
where 1=1


		and DeleteFlag1 ${deleteflagOperator} #{deleteflag}


		and Updatetime1 ${updatetimeOperator} #{updatetime}


		and Updatetime1 >= #{updatetime1}


		and Updatetime1 <= #{updatetime2}


		and Createtime1 ${createtimeOperator} #{createtime}


		and Createtime1 >= #{createtime1}


		and Createtime1 <= #{createtime2}


		and AnnounceCreator1 ${announcecreatorOperator} #{announcecreator}


		and State1 ${stateOperator} #{state}


		and ExpriyTime1 ${expriytimeOperator} #{expriytime}


		and ExpriyTime1 >= #{expriytime1}


		and ExpriyTime1 <= #{expriytime2}


		and CONTENT1 ${contentOperator} #{content}


		and TITLE1 ${titleOperator} #{title}


		and ANNOUNCEID ${pkannounceOperator} #{pkannounce}

