mapper.sql:
where 1=1


    and pk_websiteconfiguration ${pkWebsiteconfigurationOperator} #{pkWebsiteconfiguration}


    and configurationtype ${configurationtypeOperator} #{configurationtype}


    and title ${titleOperator} #{title}


    and imageurl ${imageurlOperator} #{imageurl}


    and content ${contentOperator} #{content}


    and processsequencenumber ${processsequencenumberOperator} #{processsequencenumber}


    and explicitstate ${explicitstateOperator} #{explicitstate}


    and limitquantity ${limitquantityOperator} #{limitquantity}


    and effective ${effectiveOperator} #{effective}


    and createtime ${createtimeOperator} #{createtime}


    and createtime >= #{createtime1}


    and createtime <= #{createtime2}


    and updatetime ${updatetimeOperator} #{updatetime}


    and updatetime >= #{updatetime1}


    and updatetime <= #{updatetime2}


    and deleteflag ${deleteflagOperator} #{deleteflag}

mapper.select:
select  from
	(select tmp_tb.* , rownum rn from
		(select  from TS_WEBSITECONFIGURATION


		 	${pager.orderCondition}

		) tmp_tb where rownum <= ${pager.endIndex})
		where rn >= ${pager.startIndex}
